/**
 * Signed 16 bit integer
 */
type i16 = number;

/**
 * Signed 32 bit integer
 */
type i32 = number;

/**
 * Signed 64 bit integer
 */
type i64 = number;

/**
 * Signed 16 bit float
 */
type f16 = number;

/**
 * Signed 32 bit float
 */
type f32 = number;

/**
 * Signed 64 bit float
 */
type f64 = number;

/**
 * Char type,
 * takes up a single byte in memory
 */
type char = string;
